var auxDBs = SQLDatabaseManager.getDatabases();
var activeDB = null;
var auxRecords = null;
var auxQuery = "";

for (var item in auxDBs){
	System.log(auxDBs[item].name);
	if (auxDBs[item].name == SQLName){
	    System.log("Database found");
		activeDB = auxDBs[item];
	}
}

if (activeDB == null){
    System.log("Error: DB not found (" +SQLName +")");
    return false;
}else{
    //Inserting new record
    auxQuery = "INSERT INTO CMDB (CAS_PROJECT, VM_NAME, ASSET_STATUS) ";
    auxQuery += "VALUES ('" +CAS_Project +"','" +VM_NAME +"','CREATED')";
    System.log(auxQuery);
    auxRecords = activeDB.executeCustomQuery(auxQuery);
    if (auxRecords != 1){
        System.log("Error, assest not created");
        return false;
    }else{
        System.log("Asset created successfully");
    }
}
return true;
